 /// Call the dataTables jQuery plugin///
////////////////////////////////////////

//resto//(ADMIN)//
$(document).ready(function() {
  var t = $('#restoDataTable').DataTable({
      destroy: true,
      "columnDefs": [{
          "searchable": false,
          "orderable": false,
          "targets": [0,4,5]
      }],
      "lengthMenu": [[5, 10, 15, -1], [5, 10, 15, "All"]],
      "order": [
          [1, 'asc']
      ]
  });

  t.on('order.dt search.dt', function() {
      t.column(0, {
          search: 'applied',
          order: 'applied'
      }).nodes().each(function(cell, i) {
          cell.innerHTML = i + 1;
      });
  }).draw();
});



